body.script:
const el = document.getElementById('text');
const frames = [
  "Hi ðŸ‘‹, I'm ARYAN from INDIA",
  "Hi ðŸ‘‹, I'm ARYAN from INDIA".slice(0, -1),  // backspace I
  "Hi ðŸ‘‹, I'm ARYAN from INDIA".slice(0, -2),  // backspace IA
  "Hi ðŸ‘‹, I'm ARYAN from INDIA".slice(0, -3),  // â€¦
  // continue backspacing "INDIA"
];
// Instead of manually slicing all letters, generate programmatically:
function generateFrames() {
  const init = "Hi ðŸ‘‹, I'm ARYAN from INDIA";
  const target = "BHARAT";
  const nextInit = init.slice(0, init.length - 5); // remove "INDIA"

  let seq = [];
  // type initial full line
  seq.push(init);
  // backspace 5 letters
  for (let i = init.length - 1; i >= nextInit.length; i--) {
    seq.push(init.slice(0, i));
  }
  // type BHARAT
  for (let i = 1; i <= target.length; i++) {
    seq.push(nextInit + target.slice(0, i));
  }
  // hold BHARAT for a moment
  seq.push(nextInit + target);
  // backspace BHARAT
  for (let i = target.length - 1; i >= 0; i--) {
    seq.push(nextInit + target.slice(0, i));
  }
  // type INDIA again
  for (let i = 1; i <= 5; i++) {
    seq.push(nextInit + "INDIA".slice(0, i));
  }
  // hold INDIA
  seq.push(init);
  return seq;
}

const textSeq = generateFrames();
const TYPING_DELAY = 75;   // ms per char
const HOLD_DELAY   = 600;  // ms to hold full word

(async function animate() {
  while (true) {
    // Type/backspace through each frame
    for (let i = 0; i < textSeq.length; i++) {
      el.textContent = textSeq[i];
      // Determine delay: if it's a full-word frame, hold longer
      if (i === 0 || i === 5 + 5 /* after typing BHARAT */) {
        await new Promise(r => setTimeout(r, HOLD_DELAY));
      } else {
        await new Promise(r => setTimeout(r, TYPING_DELAY));
      }
    }
  }
})();
